    function hasRole(address who, bytes32 role) external view returns (bool);
}","keccak256":"0x4e15b5def056031305e14ea9f72f176f3330233f7e90d6a8ff5d15e6ef2bd9cd","license":"MIT"},"contracts/diamonds/facetsFramework/diamondBase/libraries/InitializerLib.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

library InitializerLib {
    error AlreadyInitialized();
    error NotImplemented();

    bytes32 constant INITIALIZER_STORAGE_POSITION = keccak256("diamond.standard.diamond.storage.locus.initializer");

    struct Storage {
        bool initialized;
    }

    function get() internal pure returns (Storage storage s) {
        bytes32 position = INITIALIZER_STORAGE_POSITION;
        assembly {
            s.slot := position
        }
    }

    function reset() internal {
        get().initialized = false;
    }

    function initialize() internal {
        if (get().initialized) {
            revert AlreadyInitialized();
        } else {
            get().initialized = true;
        }
    }
}","keccak256":"0x28a20d1e0e0cfcbbe6c6e10bcdf2e138c97a92dfd34a5b2c5c9950c5b982dd3b","license":"MIT"},"contracts/diamonds/facetsFramework/diamondBase/libraries/PausabilityLib.sol":{"content":"// SPDX-License-Identifier: MIT

